              {canEdit &&
                <div className={css(styles.Sidebar_iconButton)} onClick={() => this.setState({ interestEditModalOpen: true })}>
                  <Icon name='edit' />
                </div>}
            </div>
            {currentUser && currentUser.keywords && currentUser.keywords.length > 0 ? (
