import React, { PureComponent } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import moment from 'moment';
import _ from 'lodash';
import { css, StyleSheet } from 'aphrodite';
import { Container, Button, Grid } from 'semantic-ui-react';

import { COLORS, ICONS, WINDOW_DIMENSIONS } from '../../../../constants';
import verifiedPartnerIcon from '../../../../images/icons/verified_partner_icon.svg';
import heartIcon from '../../../../images/icons/heart.svg';
import filledHeartIcon from '../../../../images/icons/filled_heart.svg';
import backgroundImage from '../../../../images/backgrounds/droplet-background.svg';
import beanImage from '../../../../images/backgrounds/bean.svg';
import haloLogo from '../../../../images/logos/halo_logo.svg';
import {
  loadCompany as loadCompanyAction,
  saveRFP as saveRFPAction,
  unsaveRFP as unsaveRFPAction,
  initializeSaveRFPs as initializeSaveRFPsAction,
  followCompany as followCompanyAction,
  unfollowCompany as unfollowCompanyAction,
  initializeFollowedCompanies as initializeFollowedCompaniesAction,
} from '../../actions/defaultActions';
import {
  toggleLoginModal as toggleLoginModalAction,
} from '../../actions/profileActions';

const { TABLET_LANDSCAPE_MEDIA_QUERY } = WINDOW_DIMENSIONS;

const styles = StyleSheet.create({
  CompanyPage_container: {
    width: '85%',
    margin: 'auto',
    zIndex: 100,
    [TABLET_LANDSCAPE_MEDIA_QUERY]: {
      width: '90%',
    },
  },
  CompanyPage_company: {
    background: COLORS.haloGray,
    height: '511px',
    position: 'relative',
    overflow: 'hidden',
    [TABLET_LANDSCAPE_MEDIA_QUERY]: {
      background: COLORS.white,
      textAlign: 'left',
      height: 'auto',
    },
  },
  CompanyPage_companyLink: {
    color: COLORS.haloBlack,
    fontSize: '36px',
    lineHeight: '48px',
    fontWeight: 'bold',
    fontFamily: '-apple-system, BlinkMacSystemFont',
    [TABLET_LANDSCAPE_MEDIA_QUERY]: {
      fontSize: '24px',
      lineHeight: '36px',
    },
  },
  CompanyPage_title: {
    color: COLORS.haloBlack,
    fontFamily: '-apple-system, BlinkMacSystemFont',
    fontWeight: 'bold',
    fontSize: '18px',
    lineHeight: '30px',
    textAlign: 'center',
    marginBottom: '8px',
    width: '100%',
  },
  CompanyPage_titleLink: {
    textDecoration: 'none',
    width: '100%',
    color: COLORS.haloBlack,
  },
  CompanyPage_summary: {
    color: COLORS.haloBlack,
    fontSize: '16px',
    lineHeight: '28px',
    marginBottom: '8px',
  },
  CompanyPage_bulletList: {
    marginLeft: '15px',
    color: COLORS.haloBlack,
    fontSize: '16px',
    lineHeight: '30px',
  },
  CompanyPage_regularFont: {
    color: COLORS.haloBlack,
    fontSize: '16px',
    lineHeight: '30px',
  },
  CompanyPage_miniSectionHeader: {
    marginLeft: '15px',
    fontWeight: 'bold',
    color: COLORS.haloBlack,
    fontSize: '16px',
    lineHeight: '30px',
    marginBottom: '20px',
  },
  CompanyPage_actionSubline: {
    fontFamily: '-apple-system, BlinkMacSystemFont',
    fontSize: '14px',
    lineHeight: '24px',
    textAlign: 'center',
    color: COLORS.lightGray,
    marginBottom: '24px',
  },
  CompanyPage_submitButton: {
    width: '90%',
    maxWidth: '90%',
    background: COLORS.lightBlue,
    backgroundImage: 'linear-gradient(134.72deg, #4E9DF5 0%, #48B2F4 100%)',
    border: `2px solid ${COLORS.lightBlue}`,
    borderRadius: '4px',
    padding: '8px 30px',
    color: COLORS.white,
    lineHeight: '15px',
    fontWeight: 500,
    fontSize: '16px',
    [TABLET_LANDSCAPE_MEDIA_QUERY]: {
      width: '90%',
    },
  },
  CompanyPage_shareButton: {
    backgroundColor: COLORS.snowWhite,
    border: `2px solid ${COLORS.gray}`,
    borderRadius: '4px',
    padding: '8px 20px',
    color: COLORS.gray,
    lineHeight: '15px',
    fontWeight: 500,
    fontSize: '13px',
    marginRight: '8px',
    marginLeft: '8px',
  },
  CompanyPage_saveButton: {
    color: COLORS.haloBlue,
    lineHeight: '15px',
    fontWeight: 500,
    fontSize: '16px',
    marginTop: '25px',
    textAlign: 'center',
  },
  CompanyPage_sectionHeader: {
    color: COLORS.lightBlack,
    fontSize: '13px',
    fontWeight: 500,
    letterSpacing: '0.5px',
    lineHeight: '20px',
    marginBottom: '16px',
  },
  CompanyPage_section: {
    marginBottom: '30px',
  },
  CompanyPage_verifiedIconContainer: {
    display: 'block',
  },
  CompanyPage_verifiedIcon: {
    display: 'inline-block',
    marginLeft: '5px',
    marginRight: '5px',
  },
  CompanyPage_verifiedIconText: {
    display: 'inline-block',
    color: COLORS.haloBlue,
    fontSize: '12px',
    lineHeight: '14px',
    fontFamily: '-apple-system, BlinkMacSystemFont',
  },
  CompanyPage_tag: {
    marginRight: '10px',
    width: '200px',
    display: 'flex',
    alignItems: 'center',
    marginBottom: '10px',
    [TABLET_LANDSCAPE_MEDIA_QUERY]: {
      width: '48%',
      marginRight: '2%',
    },
  },
  CompanyPage_tagText : {
    marginRight: '10px',
    display: 'inline-block',
    color: COLORS.haloBlack,
    fontSize: '16px',
    lineHeight: '30px',
    [TABLET_LANDSCAPE_MEDIA_QUERY]: {
      fontSize: '14px',
      lineHeight: '24px',
    },
  },
  CompanyPage_therapeuticArea: {
    fontWeight: 600,
    display: 'inline-block',
    width: '100%',
    fontSize: '12px',
